import React, { useState, useMemo, useEffect } from "react";
import axios from "axios";
import "./App.css";

const API_BASE =
  process.env.REACT_APP_API_BASE?.replace(/\/+$/, "") || "http://127.0.0.1:8000";

export default function App() {
  const [location, setLocation] = useState("");
  const [trafficCondition, setTrafficCondition] = useState("normal");
  const [incidentDetected, setIncidentDetected] = useState(false);
  const [vehicleSpeed, setVehicleSpeed] = useState("");
  const [loading, setLoading] = useState(false);
  const [resp, setResp] = useState(null); // { ok: boolean, message: string }
  const [history, setHistory] = useState([]); // ÊúÄËøëÂèëÈÄÅËÆ∞ÂΩï

  // ÂÖ∂ÂÆÉ‰∏â‰∏™Ê®°ÂùóÁöÑÁä∂ÊÄÅ
  const [weatherAlert, setWeatherAlert] = useState("");
  const [parkingLocation, setParkingLocation] = useState("");
  const [availableSpots, setAvailableSpots] = useState("");
  const [safetyStatus, setSafetyStatus] = useState("");

  // ‰∫§‰∫íËÆ∞ÂΩïÂå∫ÂüüÈ´òÂ∫¶ÔºàÂèØÊãñÊãΩÔºâ
  const [historyHeight, setHistoryHeight] = useState(260);

  // ÂèëÈÄÅÈ©æÈ©∂‰ªªÂä°ÁöÑ payload
  const payload = useMemo(
    () => ({
      location: location.trim(),
      traffic_condition: trafficCondition,
      incident_detected: incidentDetected,
      vehicle_speed: vehicleSpeed === "" ? null : Number(vehicleSpeed),
      ts: Date.now(),
    }),
    [location, trafficCondition, incidentDetected, vehicleSpeed]
  );

  const isValid = useMemo(() => {
    if (!payload.location) return false;
    if (payload.vehicle_speed === null || Number.isNaN(payload.vehicle_speed))
      return false;
    if (payload.vehicle_speed < 0) return false;
    return true;
  }, [payload]);

  // ÂèëÈÄÅÈ©æÈ©∂‰∫ã‰ª∂
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isValid) {
      setResp({ ok: false, message: "ËØ∑ÂÆåÊï¥Â°´ÂÜôË°®ÂçïÂπ∂Á°Æ‰øùÈÄüÂ∫¶‰∏∫ÈùûË¥üÊï∞„ÄÇ" });
      return;
    }
    setLoading(true);
    setResp(null);
    try {
      const { data } = await axios.post(`${API_BASE}/events/autonomous_driving`, {
        location: payload.location,
        traffic_condition: payload.traffic_condition,
        incident_detected: payload.incident_detected,
        vehicle_speed: payload.vehicle_speed,
      });
      const message = typeof data === "object" && data?.status ? String(data.status) : "success";
      setResp({ ok: true, message });
      setHistory((h) =>
        [
          {
            id: cryptoRandomId(),
            at: new Date().toLocaleString(),
            ...payload,
            message,
          },
          ...h,
        ].slice(0, 100)
      );
    } catch (err) {
      setResp({
        ok: false,
        message:
          err?.response?.data?.detail
            ? `ÂêéÁ´ØËøîÂõû: ${JSON.stringify(err.response.data.detail)}`
            : "ËØ∑Ê±ÇÂ§±Ë¥•ÔºåËØ∑Ê£ÄÊü•ÂêéÁ´ØÊòØÂê¶Â∑≤ÂêØÂä®ÊàñÊé•Âè£Ë∑ØÂæÑÊòØÂê¶Ê≠£Á°Æ„ÄÇ",
      });
    } finally {
      setLoading(false);
    }
  };

  // WebSocketÔºöÊé•Êî∂ÂêéÁ´ØÂπøÊí≠
  useEffect(() => {
    const ws = new WebSocket(`${API_BASE.replace("http", "ws")}/ws`);
    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);

      const push = (payload, message) =>
        setHistory((h) => [{ id: cryptoRandomId(), at: new Date().toLocaleString(), ...payload, message }, ...h].slice(0, 100));

      if (data.type === "autonomous_driving") push(data.payload, "received");
      if (data.type === "weather_alert") push(data.payload, "alert received");
      if (data.type === "parking_update") push(data.payload, "parking updated");
      if (data.type === "safety_monitor") push(data.payload, "safety monitoring");
    };
    return () => ws.close();
  }, []);

  // Ê∏ÖÈô§ËÆ∞ÂΩï
  const handleClearHistory = () => {
    setHistory([]);
  };

  // ÊãñÊãΩË∞ÉÊï¥‰∫§‰∫íËÆ∞ÂΩïÈ´òÂ∫¶
  const handleMouseDown = (e) => {
    const startY = e.clientY;
    const startHeight = historyHeight;

    const onMove = (ev) => {
      const diff = ev.clientY - startY;
      setHistoryHeight((h) => Math.max(150, Math.min(600, startHeight + diff))); // ÈôêÂà∂ÊúÄÂ∞èÈ´òÂ∫¶ 150pxÔºåÊúÄÂ§ßÈ´òÂ∫¶ 600px
    };
    const onUp = () => {
      window.removeEventListener("mousemove", onMove);
      window.removeEventListener("mouseup", onUp);
    };
    window.addEventListener("mousemove", onMove);
    window.addEventListener("mouseup", onUp);
  };

  return (
    <div className="page">
      <header className="page__header">
        <h1>Ëá™Âä®È©æÈ©∂ √ó ÂüéÂ∏ÇÁÆ°ÁêÜÊéßÂà∂Èù¢Êùø</h1>
