import React, { useEffect, useMemo, useState } from "react";
import axios from "axios";
import "./App.css";

/**
 * ÂêéÁ´ØÁ´ØÂè£Áªü‰∏ÄÂà∞ 8000Ôºà‰∏é‰Ω†ÂΩìÂâç uvicorn Êó•Âøó‰∏ÄËá¥Ôºâ
 * Â¶ÇÊûúÊîπÊàêÂÖ∂ÂÆÉÁ´ØÂè£ÔºàÂ¶Ç 2003ÔºâÔºåÂè™ÈúÄË¶Å‰øÆÊîπ API_BASEÔºåWS_URL ‰ºöËá™Âä®Ë∑üÁùÄÊîπ„ÄÇ
 */
const API_BASE =
  (process.env.REACT_APP_API_BASE &&
    process.env.REACT_APP_API_BASE.replace(/\/+$/, "")) ||
  "http://localhost:8000";

// Ëá™Âä®ÁîüÊàê WebSocket URLÔºåÈÅøÂÖçÂøòËÆ∞ÂêåÊ≠•ÊîπÁ´ØÂè£
const WS_URL = API_BASE.replace("http", "ws") + "/ws";

export default function App() {
  const [location, setLocation] = useState("");
  const [weatherAlert, setWeatherAlert] = useState("");
  const [safetyStatus, setSafetyStatus] = useState("ok");
  const [loading, setLoading] = useState(false);

  const [resp, setResp] = useState(null);
  const [history, setHistory] = useState([]);
  const [historyHeight, setHistoryHeight] = useState(300);

  const pushHistory = (payload, message) => {
    setHistory((h) => [
      {
        id: cryptoId(),
        at: new Date().toLocaleString(),
        ...payload,
        message,
      },
      ...h,
    ]);
  };

  // WebSocket Âè™Ëøû‰∏ÄÊ¨°
  useEffect(() => {
    const ws = new WebSocket(WS_URL);

    ws.onopen = () => console.log("[WS] connected", WS_URL);

    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        switch (data.type) {
          case "weather_alert":
            pushHistory({ ...data.payload }, "Weather alert sent");
            break;
          case "safety_monitor":
            pushHistory({ ...data.payload }, "Safety status received");
            break;
          case "city_report":
            pushHistory(
              { report: data.payload?.report },
              "City report generated"
            );
            break;
          default:
            pushHistory({ raw: data }, `WS:${data.type || "unknown"}`);
        }
      } catch (e) {
        console.warn("[WS message parse error]", e);
      }
    };

    ws.onerror = (e) => console.error("[WS] error", e);
    ws.onclose = () => console.log("[WS] closed");

    return () => ws.close();
  }, []);

  // ÂèëÈÄÅÂ§©Ê∞îÂëäË≠¶
  const onSendWeatherAlert = async (e) => {
    e.preventDefault();
    if (!location.trim() || !weatherAlert.trim()) {
      setResp({ ok: false, message: "ËØ∑Â°´ÂÜô Location ‰∏é Weather Alert" });
      return;
    }
    setLoading(true);
    setResp(null);
    try {
      const { data } = await axios.post(`${API_BASE}/events/weather_alert`, {
        alert_type: weatherAlert,
        area: location,
      });
