import React, { useState, useMemo, useEffect } from "react";
import axios from "axios";
import "./App.css";

const API_BASE =
  process.env.REACT_APP_API_BASE?.replace(/\/+$/, "") || "http://localhost:8080"; // ËøôÈáå‰ΩøÁî®ÂêéÁ´ØÁöÑÁ´ØÂè£

export default function App() {
  const [location, setLocation] = useState("");
  const [trafficCondition, setTrafficCondition] = useState("normal");
  const [incidentDetected, setIncidentDetected] = useState(false);
  const [vehicleSpeed, setVehicleSpeed] = useState("");
  const [loading, setLoading] = useState(false);
  const [resp, setResp] = useState(null); // { ok: boolean, message: string }
  const [history, setHistory] = useState([]); // ÊúÄËøëÂèëÈÄÅËÆ∞ÂΩï

  const [weatherAlert, setWeatherAlert] = useState("");
  const [safetyStatus, setSafetyStatus] = useState("");
  const [historyHeight, setHistoryHeight] = useState(260);

  // ÂèëÈÄÅÈ©æÈ©∂‰ªªÂä°ÁöÑ payload
  const payload = useMemo(
    () => ({
      location: location.trim(),
      traffic_condition: trafficCondition,
      incident_detected: incidentDetected,
      vehicle_speed: vehicleSpeed === "" ? null : Number(vehicleSpeed),
      ts: Date.now(),
    }),
    [location, trafficCondition, incidentDetected, vehicleSpeed]
  );

  const isValid = useMemo(() => {
    if (!payload.location) return false;
    if (payload.vehicle_speed === null || Number.isNaN(payload.vehicle_speed))
      return false;
    if (payload.vehicle_speed < 0) return false;
    return true;
  }, [payload]);

  // WebSocketÔºöÊé•Êî∂ÂêéÁ´ØÂπøÊí≠
  useEffect(() => {
    const ws = new WebSocket("ws://localhost:8080/ws");

    ws.onopen = () => {
      console.log("WebSocket connection established");
    };

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);

      const push = (payload, message) =>
        setHistory((h) => [
          { id: cryptoRandomId(), at: new Date().toLocaleString(), ...payload, message },
          ...h,
        ].slice(0, 100));

      if (data.type === "safety_monitor") push(data.payload, "received safety status");
      if (data.type === "fire_response") push(data.payload, "fire response triggered");
      if (data.type === "city_report") push(data.payload, "city report generated");
    };

    return () => ws.close();
  }, []);

  // ÂèëÈÄÅÂ§©Ê∞îË≠¶Âëä
  const handleWeatherAlertSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setResp(null);
    try {
      const { data } = await axios.post(`${API_BASE}/events/weather_alert`, {
        alert_type: weatherAlert,
        location: location,
      });
      setResp({ ok: true, message: data.status });
      setHistory((h) => [
        {
          id: cryptoRandomId(),
          at: new Date().toLocaleString(),
          location,
          alert_type: weatherAlert,
          message: "Weather alert sent",
        },
        ...h,
      ]);
    } catch (err) {
      setResp({
        ok: false,
        message:
          err?.response?.data?.detail
            ? `ÂêéÁ´ØËøîÂõû: ${JSON.stringify(err.response.data.detail)}`
            : "ËØ∑Ê±ÇÂ§±Ë¥•ÔºåËØ∑Ê£ÄÊü•ÂêéÁ´ØÊòØÂê¶Â∑≤ÂêØÂä®ÊàñÊé•Âè£Ë∑ØÂæÑÊòØÂê¶Ê≠£Á°Æ„ÄÇ",
      });
    } finally {
      setLoading(false);
    }
  };

  // ÂÆâÂÖ®Â∑°Ê£ÄÁªìÊûúÂ§ÑÁêÜ
  const handleSafetyInspectionResult = (safetyStatus) => {
    console.log("Safety inspection result:", safetyStatus);
    // Fire response logic goes here
  };

  // ÂèëÈÄÅÂÆâÂÖ®Â∑°Ê£ÄËØ∑Ê±Ç
  const handleSafetyMonitorSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setResp(null);
    try {
      const { data } = await axios.post(`${API_BASE}/events/safety_monitor`, {
        location,
        safety_status: safetyStatus,
      });
      setResp({ ok: true, message: data.status });
      setHistory((h) => [
        {
          id: cryptoRandomId(),
          at: new Date().toLocaleString(),
          location,
          safety_status: safetyStatus,
          message: "Safety status sent",
        },
