import React, { useState, useEffect, useMemo } from "react";
import axios from "axios";
import "./App.css";

const API_BASE =
  process.env.REACT_APP_API_BASE?.replace(/\/+$/, "") || "http://localhost:8000"; // ÂêéÁ´ØÁ´ØÂè£

export default function App() {
  const [location, setLocation] = useState("");
  const [weatherAlert, setWeatherAlert] = useState("");
  const [safetyStatus, setSafetyStatus] = useState("ok");
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [resp, setResp] = useState(null);

  const [historyHeight, setHistoryHeight] = useState(260);

  // WebSocketÔºöÊé•Êî∂ÂêéÁ´ØÂπøÊí≠
  useEffect(() => {
    const ws = new WebSocket("ws://localhost:3002/ws");

    ws.onopen = () => {
      console.log("WebSocket connection established");
    };

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      const push = (payload, message) =>
        setHistory((h) => [
          { id: cryptoRandomId(), at: new Date().toLocaleString(), ...payload, message },
          ...h,
        ].slice(0, 100));

      if (data.type === "safety_monitor") push(data.payload, "received safety status");
      if (data.type === "fire_response") push(data.payload, "fire response triggered");
      if (data.type === "city_report") push(data.payload, "city report generated");
    };

    return () => ws.close();
  }, []);

  // ÂèëÈÄÅÂ§©Ê∞îË≠¶Âëä
  const handleWeatherAlertSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setResp(null);
    try {
      const { data } = await axios.post(`${API_BASE}/events/weather_alert`, {
        alert_type: weatherAlert,
        area: location,
      });
      setResp({ ok: true, message: data.status });
      setHistory((h) => [
        { id: cryptoRandomId(), at: new Date().toLocaleString(), location, alert_type: weatherAlert, message: "Weather alert sent" },
        ...h,
      ]);
    } catch (err) {
      setResp({ ok: false, message: "ËØ∑Ê±ÇÂ§±Ë¥•ÔºåËØ∑Ê£ÄÊü•ÂêéÁ´ØÊòØÂê¶Â∑≤ÂêØÂä®ÊàñÊé•Âè£Ë∑ØÂæÑÊòØÂê¶Ê≠£Á°Æ„ÄÇ" });
    } finally {
      setLoading(false);
    }
  };

  // ÁîüÊàêÂüéÂ∏ÇÊä•Âëä
  const handleGenerateReport = async () => {
    const reportData = { weatherAlert: "Êö¥Èõ®", safetyStatus: "OK", location: "Macau", trafficCondition: "Normal" };
    try {
      const response = await axios.post("http://localhost:3002/generate-report", reportData);
      console.log("Generated Report:", response.data.report);
    } catch (error) {
      console.error("Error generating report:", error);
    }
  };

  // Ëé∑ÂèñÈöèÊú∫ID
  function cryptoRandomId() {
    if (window.crypto?.randomUUID) return window.crypto.randomUUID();
    return "id-" + Math.random().toString(36).slice(2);
  }

  return (
    <div className="page">
      <header className="page__header">
        <h1>Ëá™Âä®È©æÈ©∂ √ó ÂüéÂ∏ÇÁÆ°ÁêÜÊéßÂà∂Èù¢Êùø</h1>
        <p className="subtitle">Â∑¶‰æßÂõõ‰∏™Ê®°ÂùóÂèëËµ∑‰ªªÂä° / Âè≥‰æßÊü•ÁúãÂ§öÊô∫ËÉΩ‰Ωì‰∫§‰∫íËÆ∞ÂΩï</p>
      </header>

      <main className="page__main">
        <section className="modules">
          {/* ÂèëÈÄÅÂ§©Ê∞îË≠¶Âëä */}
          <section className="card">
            <h2 className="card__title">ÂèëÈÄÅÂ§©Ê∞îË≠¶Âëä</h2>
            <form onSubmit={handleWeatherAlertSubmit} className="form">
              <label>
                <span>Location</span>
                <input
                  type="text"
                  value={location}
                  onChange={(e) => setLocation(e.target.value)}
                  placeholder="‰æãÂ¶ÇÔºöMacau"
                  required
                />
              </label>

              <label>
                <span>Weather Alert</span>
                <input
                  type="text"
                  value={weatherAlert}
                  onChange={(e) => setWeatherAlert(e.target.value)}
                  placeholder="‰æãÂ¶ÇÔºöÊö¥Èõ®"
                  required
                />
              </label>

              <button type="submit" className="btn" disabled={loading}>
                {loading ? "Sending..." : "Send Alert"}
              </button>
            </form>
          </section>

          {/* ÂÆâÂÖ®Â∑°Ê£Ä */}
          <section className="card">
            <h2 className="card__title">ÂÆâÂÖ®Â∑°Ê£Ä</h2>
            <form className="form">
              <label>
                <span>Location</span>
                <input
                  type="text"
                  value={location}
                  onChange={(e) => setLocation(e.target.value)}
                  placeholder="‰æãÂ¶ÇÔºöMacau"
                  required
                />
              </label>

              <label>
                <span>Safety Status</span>
                <select
                  value={safetyStatus}
                  onChange={(e) => setSafetyStatus(e.target.value)}
                >
                  <option value="ok">OK</option>
                  <option value="warning">Warning</option>
                  <option value="incident">Incident</option>
                </select>
              </label>

              <button type="button" className="btn" onClick={handleGenerateReport} disabled={loading}>
                ÁîüÊàêÂüéÂ∏ÇÂàÜÊûêÊä•Âëä
              </button>
            </form>
          </section>
        </section>

        <section className="interaction-record">
          <section className="card">
            <h2 className="card__title">‰∫§‰∫íËÆ∞ÂΩï</h2>
            <div className="history-scroll" style={{ height: `${historyHeight}px` }}>
              {history.length === 0 ? (
                <p className="muted">ÊöÇÊó†ËÆ∞ÂΩïÔºåÂèëÈÄÅ‰ªª‰∏ÄÊ®°ÂùóÂêéÂ∞ÜÂú®Ê≠§ÊòæÁ§∫„ÄÇ</p>
              ) : (
                <ul className="history">
                  {history.map((h) => (
                    <li key={h.id}>
                      <div className="history__line">
                        <span className="pill">{h.traffic_condition}</span>
                        {h.incident_detected && <span className="tag">incident</span>}
                        <span className="loc">{h.location || h.area || "-"}</span>
                      </div>
                      <div className="history__meta">
                        <span>{h.at}</span>
                        {typeof h.vehicle_speed !== "undefined" && <span>{h.vehicle_speed} km/h</span>}
                        {typeof h.available_spots !== "undefined" && <span>{h.available_spots} spots</span>}
                        {h.alert_type && <span>‚ö† {h.alert_type}</span>}
                        {h.safety_status && <span>üõ° {h.safety_status}</span>}
                        <span className={h.message === "received" ? "ok" : ""}>
                          {h.message}
                        </span>
                      </div>
                    </li>
                  ))}
                </ul>
              )}
            </div>
          </section>
        </section>
      </main>
    </div>
